public class MapExample {

    private final Map<String, Integer> map = new HashMap<>();
    private final Lock lock = new ReentrantLock();

    public void put(String key, int value) {
        lock.lock();
        try {
            map.put(key, value);
        } finally {
            lock.unlock();
        }
    }

